import React, { useState } from 'react';
import { createBid, updateAuctionItemDiscount } from '../services/api';

const AuctionItemDetail = ({ item }) => {
    const [amount, setAmount] = useState('');
    const [userId, setUserId] = useState('');
    const [discount, setDiscount] = useState(item?.discount || 0);

    const handleBid = async () => {
        try {
            const bid = { amount, userId };
            await createBid(item.id, bid);
            // Handle bid success
        } catch (error) {
            // Handle bid error
            console.error("Error placing bid:", error);
        }
    };

    const handleDiscountChange = async (newDiscount) => {
        try {
            await updateAuctionItemDiscount(item.id, newDiscount);
            setDiscount(newDiscount);
        } catch (error) {
            // Handle discount update error
            console.error("Error updating discount:", error);
        }
    };

    if (!item) {
        return <div>No item selected</div>;
    }

    return (
        <div>
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <p>Starting Price: ${item.startingPrice}</p>
            <p>Discount: {discount}%</p>
            <p>Discounted Price: ${item.startingPrice - (item.startingPrice * discount / 100)}</p>
            <div>
                <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter bid amount" />
                <input type="text" value={userId} onChange={(e) => setUserId(e.target.value)} placeholder="Enter user ID" />
                <button onClick={handleBid}>Place Bid</button>
            </div>
            <div>
                <button onClick={() => handleDiscountChange(10)}>10% Discount</button>
                <button onClick={() => handleDiscountChange(25)}>25% Discount</button>
                <button onClick={() => handleDiscountChange(50)}>50% Discount</button>
            </div>
        </div>
    );
};

export default AuctionItemDetail;
