import React, { useEffect, useState } from 'react';
import { getAuctionItemById, updateAuctionItem } from '../services/api';
import { Card, Button, ButtonGroup, Form } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';

const AuctionItemEdit = () => {
    const { itemId } = useParams();
    const navigate = useNavigate();
    const [item, setItem] = useState(null);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState(0);
    const [startingBid, setStartingBid] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (itemId) {
            console.log('Fetching item details for itemId:', itemId);
            getAuctionItemById(itemId).then(response => {
                console.log('Fetched item details:', response.data);
                setItem(response.data);
                setName(response.data.name);
                setDescription(response.data.description);
                setPrice(response.data.price);
                setStartingBid(response.data.startingBid);
                setLoading(false);
            }).catch(error => {
                console.error('Error fetching auction item details:', error);
                setError(error.message);
                setLoading(false);
            });
        }
    }, [itemId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'name') setName(value);
        if (name === 'description') setDescription(value);
        if (name === 'price') setPrice(value);
        if (name === 'startingBid') setStartingBid(value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const updatedItem = { ...item, name, description, price, startingBid };
        try {
            const response = await updateAuctionItem(item.id, updatedItem);
            console.log('Update response:', response.data);
            navigate(`/items/${item.id}`);
        } catch (error) {
            console.error('Error updating auction item:', error);
            setError(error.message);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="col-md-8">
            <Card>
                <Card.Body>
                    <Card.Title>Edit Auction Item</Card.Title>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formItemName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="name"
                                value={name}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formItemDescription" className="mt-3">
                            <Form.Label>Description</Form.Label>
                            <Form.Control
                                as="textarea"
                                name="description"
                                value={description}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formItemPrice" className="mt-3">
                            <Form.Label>Price</Form.Label>
                            <Form.Control
                                type="number"
                                name="price"
                                value={price}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formStartingBid" className="mt-3">
                            <Form.Label>Starting Bid</Form.Label>
                            <Form.Control
                                type="number"
                                name="startingBid"
                                value={startingBid}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <ButtonGroup className="mt-3">
                            <Button variant="secondary" onClick={() => setPrice(price * 0.9)}>10%</Button>
                            <Button variant="secondary" onClick={() => setPrice(price * 0.75)}>25%</Button>
                            <Button variant="secondary" onClick={() => setPrice(price * 0.5)}>50%</Button>
                            <Button variant="secondary" onClick={() => setPrice(price * 0.25)}>75%</Button>
                        </ButtonGroup>
                        <Button variant="primary" type="submit" className="mt-3">
                            Save Changes
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    );
};

export default AuctionItemEdit;
