import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import AuctionItemList from './components/AuctionItemList';
import AuctionItemDetail from './components/AuctionItemDetail';
import AuctionItemDetailEdit from './components/AuctionItemDetailEdit';
import { Container, Row, Col, Button } from 'react-bootstrap';

const App = () => {
    const [selectedItemId, setSelectedItemId] = useState(null);

    return (
        <Router>
            <Container>
                <h1 className="my-4">Auction App</h1>
                <Row>
                    <Col md={4}>
                        <AuctionItemList onSelectItem={setSelectedItemId} />
                    </Col>
                    <Col md={8}>
                        <Switch>
                            <Route path="/edit/:itemId">
                                <AuctionItemDetailEdit itemId={selectedItemId} />
                            </Route>
                            <Route path="/">
                                <AuctionItemDetail itemId={selectedItemId} />
                            </Route>
                        </Switch>
                    </Col>
                </Row>
            </Container>
        </Router>
    );
};

export default App;
